mapper.update:
UPDATE orders
SET user_id = #{userId}, order_price = #{orderPrice}, zipcode = #{zipcode}, main_address = #{mainAddress}, detail_address = #{detailAddress}, order_enroll = #{orderEnroll}, order_status = #{orderStatus}, total_item_count = #{totalItemCount}
WHERE order_id = #{orderId}

UPDATE orders
SET order_status=#{status}
WHERE order_id = #{orderId}

UPDATE orders
SET tid=#{tid}
WHERE order_id = #{orderId}
